package ir.gonabad.core.dataimport ir.gonabad.core.domain.addCategory.AddCategoryResponseimport ir.gonabad.core.domain.addTask.AddTaskResponseimport ir.gonabad.core.domain.deleteCategory.DeleteCategoryResponseimport ir.gonabad.core.domain.deleteUser.DeleteUserResponseimport ir.gonabad.core.domain.listCategory.CategoryListimport ir.gonabad.core.domain.listUser.UserListResponseimport ir.gonabad.core.domain.login.Logininterface UserRemoteDataSource {    suspend fun login(username : String , password : String) : Login    suspend fun register(username: String , password: String , type : String , token : String) : Login    suspend fun getCategory(token: String) : CategoryList    suspend fun addCategory(title : String , token : String) : AddCategoryResponse    suspend fun editCategory(id : String ,title : String , token : String) : AddCategoryResponse    suspend fun deleteCategory (id : String , token : String) : DeleteCategoryResponse    suspend fun getUserList(token : String) : UserListResponse    suspend fun deleteUser(username: String , token: String) : DeleteUserResponse    suspend fun addTask(        categoryId : String ,        title : String ,        descreption : String ,        status : String ,        days : String ,        users : String ,        token: String    ) : AddTaskResponse}