package ir.gonabad.core.intractorsimport ir.ghadamyaar.aminsoft.stepcounter.helper.DataStateimport ir.gonabad.core.data.UserRepositoryimport ir.gonabad.core.domain.addCategory.AddCategoryResponseimport kotlinx.coroutines.flow.Flowclass AddCategory(private val userRepository: UserRepository) {    suspend operator fun invoke(title : String , token: String , isOnline : Boolean) : Flow<DataState<AddCategoryResponse>> = userRepository.addCategory(title ,token , isOnline)}