package ir.gonabad.core.intractorsimport ir.ghadamyaar.aminsoft.stepcounter.helper.DataStateimport ir.gonabad.core.data.UserRepositoryimport ir.gonabad.core.domain.addCategory.AddCategoryResponseimport ir.gonabad.core.domain.addTask.AddTaskResponseimport kotlinx.coroutines.flow.Flowclass AddTask(private val userRepository: UserRepository) {    suspend operator fun invoke(        categoryId : String ,        title : String ,        descreption : String ,        status : String ,        days : String ,        users : String ,        token: String,        isOnline: Boolean    ) : Flow<DataState<AddTaskResponse>> = userRepository.addTask(categoryId, title, descreption, status, days, users, token, isOnline)}