package ir.gonabad.core.intractorsimport ir.ghadamyaar.aminsoft.stepcounter.helper.DataStateimport ir.gonabad.core.data.UserRepositoryimport ir.gonabad.core.domain.addCategory.AddCategoryResponseimport ir.gonabad.core.domain.addTask.AddTaskResponseimport ir.gonabad.core.domain.editTask.EditTaskResponseimport kotlinx.coroutines.flow.Flowclass ChangeStatusTask(private val userRepository: UserRepository) {    suspend operator fun invoke(        taskId : String ,        descreption : String ,        status : String ,        token: String,        isOnline: Boolean    ) : Flow<DataState<EditTaskResponse>> =        userRepository.changeStatusTask(taskId, descreption, status, token, isOnline)}