package ir.gonabad.core.intractorsimport ir.ghadamyaar.aminsoft.stepcounter.helper.DataStateimport ir.gonabad.core.data.UserRepositoryimport ir.gonabad.core.domain.addCategory.AddCategoryResponseimport kotlinx.coroutines.flow.Flowclass EditCategory(private val userRepository: UserRepository) {    suspend operator fun invoke(id : String ,title : String , token: String , isOnline : Boolean) : Flow<DataState<AddCategoryResponse>> = userRepository.editCategory(id ,title ,token , isOnline)}