package ir.gonabad.core.intractorsimport ir.ghadamyaar.aminsoft.stepcounter.helper.DataStateimport ir.gonabad.core.data.UserRepositoryimport ir.gonabad.core.domain.listTask.TaskListResponseimport ir.gonabad.core.domain.listUser.UserListResponseimport kotlinx.coroutines.flow.Flowclass GetTaskList(private val userRepository: UserRepository) {    suspend operator fun invoke(categoryId : String ,token: String , isOnline : Boolean) : Flow<DataState<TaskListResponse>> = userRepository.getTaskList(categoryId ,token , isOnline)}