package ir.gonabad.core.intractorsimport ir.ghadamyaar.aminsoft.stepcounter.helper.DataStateimport ir.gonabad.core.data.UserRepositoryimport ir.gonabad.core.domain.login.Loginimport kotlinx.coroutines.flow.Flowclass LoginUser(private val userRepository: UserRepository) {    suspend operator fun invoke(username : String , pasword : String , isOnline : Boolean) : Flow<DataState<Login>> = userRepository.login(username , pasword , isOnline)}