package ir.gonabad.core.intractorsimport ir.ghadamyaar.aminsoft.stepcounter.helper.DataStateimport ir.gonabad.core.data.UserRepositoryimport ir.gonabad.core.domain.login.Loginimport kotlinx.coroutines.flow.Flowclass RegisterUser(private val userRepository: UserRepository) {    suspend operator fun invoke(username : String , password : String , type : String , token : String , isOnline : Boolean) : Flow<DataState<Login>> = userRepository.register(username , password , type , token , isOnline)}