package ir.gonabad.taskmanagmentimport android.app.Applicationimport dagger.hilt.android.HiltAndroidAppimport ir.gonabad.core.intractors.GetTokenimport ir.gonabad.core.intractors.GetTypeimport ir.gonabad.core.intractors.GetUsernameimport javax.inject.Inject@HiltAndroidAppclass MyApp : Application() {    @Inject    lateinit var getToken: GetToken    @Inject    lateinit var getUsername: GetUsername    @Inject    lateinit var getType: GetType    override fun onCreate() {        super.onCreate()        getToken.invoke()        getType.invoke()        getUsername.invoke()    }}