package ir.gonabad.taskmanagment.diimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport ir.gonabad.core.data.UserRepositoryimport ir.gonabad.taskmanagment.framework.db.UserLocalDataSourceImplimport ir.gonabad.taskmanagment.framework.network.UserRemoteDataSourceImplimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)class RepositoryModule {    @Provides    @Singleton    fun provideUserRepository(        userLocalDataSource: UserLocalDataSourceImpl,        userRemoteDataSourceImpl: UserRemoteDataSourceImpl    ): UserRepository {        return UserRepository(            userRemoteDataSourceImpl ,            userLocalDataSource        )    }}