package ir.gonabad.taskmanagment.diimport android.content.Contextimport android.graphics.Typefaceimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.android.qualifiers.ApplicationContextimport dagger.hilt.components.SingletonComponentimport ir.gonabad.taskmanagment.utils.Constantsimport ir.gonabad.taskmanagment.di.qualifier.BoldTypefaceimport ir.gonabad.taskmanagment.di.qualifier.MediumTypefaceimport javax.inject.Namedimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)class TypefaceModule {    @Provides    @Named("type_face_medium")    @Singleton    fun provideTypeFaceRegularGilroy(@ApplicationContext context: Context) : Typeface{        return Typeface.createFromAsset(context.assets, Constants.mediumFont)    }    @Provides    @Named("type_face_bold")    @Singleton    fun provideTypeFaceBoldGilroy(@ApplicationContext context: Context) : Typeface{        return Typeface.createFromAsset(context.assets, Constants.boldFont)    }    @Provides    @BoldTypeface    fun provideTypeFaceBold(@Named("type_face_bold") fontBold:Typeface) : Typeface{        return fontBold    }    @Provides    @MediumTypeface    fun provideTypeFaceMedium(@Named("type_face_medium") fontMedium:Typeface) : Typeface{        return fontMedium    }}