package ir.gonabad.taskmanagment.framework.dbimport ir.gonabad.core.data.UserLocalDataSourceimport ir.gonabad.taskmanagment.utils.PrefHandlerimport ir.gonabad.taskmanagment.utils.UserInfoContainerimport javax.inject.Injectclass UserLocalDataSourceImpl @Inject constructor(    private val preferenceHandler: PrefHandler,    ) : UserLocalDataSource {    override fun setToken(token: String) {        preferenceHandler.setPreference(preferenceHandler.KEY_TOKEN , token)    }    override fun getToken() {        preferenceHandler.getString(preferenceHandler.KEY_TOKEN , null)?.let{            UserInfoContainer.update(it)        }    }    override fun setType(type: String) {        preferenceHandler.setPreference(preferenceHandler.KEY_TYPE , type)    }    override fun getType() {        preferenceHandler.getString(preferenceHandler.KEY_TYPE , null)?.let {            UserInfoContainer.setType(it)        }    }    override fun setUsername(username: String) {        preferenceHandler.setPreference(preferenceHandler.KEY_USERNAME , username)    }    override fun getUsername() {        preferenceHandler.getString(preferenceHandler.KEY_USERNAME , null)?.let {            UserInfoContainer.setUsername(it)        }    }}