package ir.gonabad.taskmanagment.framework.networkimport ir.gonabad.core.data.UserRemoteDataSourceimport ir.gonabad.core.domain.addCategory.AddCategoryResponseimport ir.gonabad.core.domain.addTask.AddTaskResponseimport ir.gonabad.core.domain.deleteCategory.DeleteCategoryResponseimport ir.gonabad.core.domain.deleteUser.DeleteUserResponseimport ir.gonabad.core.domain.listCategory.CategoryListimport ir.gonabad.core.domain.listUser.UserListResponseimport ir.gonabad.core.domain.login.Loginimport ir.gonabad.taskmanagment.framework.network.mapper.AddCategoryMapperimport ir.gonabad.taskmanagment.framework.network.mapper.AddTaskMapperimport ir.gonabad.taskmanagment.framework.network.mapper.CategoryListMapperimport ir.gonabad.taskmanagment.framework.network.mapper.DeleteCategoryMapperimport ir.gonabad.taskmanagment.framework.network.mapper.DeleteUserMapperimport ir.gonabad.taskmanagment.framework.network.mapper.LoginMapperimport ir.gonabad.taskmanagment.framework.network.mapper.UserListMapperimport javax.inject.Injectclass UserRemoteDataSourceImpl @Inject constructor(    private val apiService: ApiService,    private val loginMapper: LoginMapper ,    private val categoryListMapper: CategoryListMapper ,    private val addCategoryMapper: AddCategoryMapper ,    private val deleteCategoryMapper: DeleteCategoryMapper ,    private val userListMapper: UserListMapper ,    private val deleteUserMapper: DeleteUserMapper ,    private val addTaskMapper: AddTaskMapper) : UserRemoteDataSource {    override suspend fun login(username: String, password: String): Login {        return loginMapper.mapFromEntity(apiService.login(username, password))    }    override suspend fun register(username: String, password: String, type: String, token: String) : Login {        return loginMapper.mapFromEntity(apiService.register(username, password , type, token))    }    override suspend fun getCategory(token: String): CategoryList {        return categoryListMapper.mapFromEntity(apiService.getCategory(token))    }    override suspend fun addCategory(title: String, token: String): AddCategoryResponse {        return addCategoryMapper.mapFromEntity(apiService.addCategory(title , token))    }    override suspend fun editCategory(        id: String,        title: String,        token: String    ): AddCategoryResponse {        return addCategoryMapper.mapFromEntity(apiService.editCategory(id, title, token))    }    override suspend fun deleteCategory(id: String, token: String): DeleteCategoryResponse {        return deleteCategoryMapper.mapFromEntity(apiService.deleteCategory(id, token))    }    override suspend fun getUserList(token: String): UserListResponse {        return userListMapper.mapFromEntity(apiService.getUsers(token))    }    override suspend fun deleteUser(username: String, token: String): DeleteUserResponse {        return deleteUserMapper.mapFromEntity(apiService.deleteUser(username, token))    }    override suspend fun addTask(        categoryId: String,        title: String,        descreption: String,        status: String,        days: String,        users: String,        token: String    ): AddTaskResponse {        return addTaskMapper.mapFromEntity(apiService.addTask(categoryId , title , descreption , status , days, users , token))    }}