package ir.gonabad.taskmanagment.framework.network.mapperimport ir.gonabad.core.domain.addCategory.AddCategoryResponseimport ir.gonabad.core.domain.addCategory.Resultimport ir.gonabad.taskmanagment.framework.network.entities.addCategory.AddCategoryResponseEntityimport ir.gonabad.taskmanagment.framework.network.entities.addCategory.ResultEntityimport ir.gonabad.taskmanagment.framework.utils.EntityMapperimport javax.inject.Injectclass AddCategoryMapper @Inject constructor() : EntityMapper<AddCategoryResponseEntity , AddCategoryResponse> {    override fun mapFromEntity(entity: AddCategoryResponseEntity): AddCategoryResponse {        return AddCategoryResponse(            status = entity.status ,            result = Result(                id = entity.result.id ,                title = entity.result.title            )        )    }    override fun mapToEntity(domainModel: AddCategoryResponse): AddCategoryResponseEntity {        return AddCategoryResponseEntity(            status = domainModel.status ,            result = ResultEntity(                id = domainModel.result.id ,                title = domainModel.result.title            )        )    }}