package ir.gonabad.taskmanagment.framework.network.mapperimport ir.gonabad.core.domain.addCategory.AddCategoryResponseimport ir.gonabad.core.domain.addCategory.Resultimport ir.gonabad.core.domain.deleteCategory.DeleteCategoryResponseimport ir.gonabad.taskmanagment.framework.network.entities.addCategory.AddCategoryResponseEntityimport ir.gonabad.taskmanagment.framework.network.entities.addCategory.ResultEntityimport ir.gonabad.taskmanagment.framework.network.entities.deleteCategory.DeleteCategoryResponseEntityimport ir.gonabad.taskmanagment.framework.utils.EntityMapperimport javax.inject.Injectclass DeleteCategoryMapper @Inject constructor() : EntityMapper<DeleteCategoryResponseEntity , DeleteCategoryResponse> {    override fun mapFromEntity(entity: DeleteCategoryResponseEntity): DeleteCategoryResponse {        return DeleteCategoryResponse(            result = entity.result ,            status = entity.status        )    }    override fun mapToEntity(domainModel: DeleteCategoryResponse): DeleteCategoryResponseEntity {        return DeleteCategoryResponseEntity(            result = domainModel.result ,            status = domainModel.status        )    }}