package ir.gonabad.taskmanagment.framework.network.mapperimport ir.gonabad.core.domain.login.Loginimport ir.gonabad.core.domain.login.Resultimport ir.gonabad.taskmanagment.framework.network.entities.login.LoginEntityimport ir.gonabad.taskmanagment.framework.network.entities.login.ResultEntityimport ir.gonabad.taskmanagment.framework.utils.EntityMapperimport javax.inject.Injectclass LoginMapper @Inject constructor() : EntityMapper<LoginEntity , Login> {    override fun mapFromEntity(entity: LoginEntity): Login {        return Login(            result = Result(                key = entity.result.key ,                username = entity.result.username ,                password = entity.result.password ,                type = entity.result.type            ) ,            status = entity.status        )    }    override fun mapToEntity(domainModel: Login): LoginEntity {        return LoginEntity(            result = ResultEntity(                key = domainModel.result.key ,                username = domainModel.result.username ,                password = domainModel.result.password ,                type = domainModel.result.type            ) ,            status = domainModel.status        )    }}