package ir.gonabad.taskmanagment.presentation.addUserimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.navigation.fragment.findNavControllerimport dagger.hilt.android.AndroidEntryPointimport ir.gonabad.taskmanagment.databinding.FragmentAddUserBindingimport ir.gonabad.taskmanagment.utils.BaseFragment@AndroidEntryPointclass AddUserFragment : BaseFragment() {    lateinit var binding : FragmentAddUserBinding    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        binding = FragmentAddUserBinding.inflate(inflater , container , false)        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        initView()    }    private fun initView() {        binding.tbAddUser.setNavigationOnClickListener {            findNavController().popBackStack()        }    }}