package ir.gonabad.taskmanagment.presentation.homeimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport ir.gonabad.taskmanagment.databinding.ItemCategoryBindingclass CategoryAdapter(private val onClickItem : () -> Unit) : RecyclerView.Adapter<CategoryAdapter.CategoryAdapterViewHolder>() {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): CategoryAdapterViewHolder {        return CategoryAdapterViewHolder(ItemCategoryBinding.inflate(LayoutInflater.from(parent.context) , parent , false))    }    override fun getItemCount(): Int {        return 10    }    override fun onBindViewHolder(holder: CategoryAdapterViewHolder, position: Int) {        holder.onBind()    }    inner class CategoryAdapterViewHolder(private val categoryBinding: ItemCategoryBinding) : RecyclerView.ViewHolder(categoryBinding.root){        fun onBind() {            categoryBinding.cvItemCategoryItem.setOnClickListener {                onClickItem()            }        }    }}