package ir.gonabad.taskmanagment.presentation.homeimport android.graphics.Typefaceimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.fragment.app.viewModelsimport androidx.navigation.fragment.findNavControllerimport androidx.recyclerview.widget.LinearLayoutManagerimport dagger.hilt.android.AndroidEntryPointimport ir.gonabad.taskmanagment.Rimport ir.gonabad.taskmanagment.databinding.FragmentHomeBindingimport ir.gonabad.taskmanagment.di.qualifier.MediumTypefaceimport ir.gonabad.taskmanagment.utils.BaseFragmentimport javax.inject.Inject@AndroidEntryPointclass HomeFragment : BaseFragment() {    lateinit var binding : FragmentHomeBinding    val viewModel : HomeViewModel by viewModels()    @Inject    @MediumTypeface    lateinit var mediumTypeFace : Typeface    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View {        binding = FragmentHomeBinding.inflate(inflater , container , false)        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        initView()    }    private fun initView() {        binding.rvHomeCategory.layoutManager = LinearLayoutManager(requireContext() , LinearLayoutManager.VERTICAL , false)        binding.rvHomeCategory.adapter = CategoryAdapter()        binding.fabHomeAddCategory.typeface = mediumTypeFace        binding.fabHomeAddCategory.setOnClickListener {            findNavController().navigate(R.id.action_homeFragment_to_addCategoryFragment)        }    }}