package ir.gonabad.taskmanagment.presentation.manageTaskimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.fragment.app.viewModelsimport androidx.navigation.fragment.findNavControllerimport dagger.hilt.android.AndroidEntryPointimport ir.gonabad.taskmanagment.databinding.FragmentManageTaskBindingimport ir.gonabad.taskmanagment.utils.BaseFragment@AndroidEntryPointclass ManageTaskFragment : BaseFragment() {    lateinit var binding : FragmentManageTaskBinding    val viewModel : ManageTaskViewModel by viewModels()    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        binding = FragmentManageTaskBinding.inflate(inflater , container , false)        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        initView()    }    private fun initView(){        binding.tbAddTask.setNavigationOnClickListener {            findNavController().popBackStack()        }    }}