package ir.gonabad.taskmanagment.presentation.settingAdminimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.fragment.app.viewModelsimport androidx.navigation.fragment.findNavControllerimport dagger.hilt.android.AndroidEntryPointimport ir.gonabad.taskmanagment.Rimport ir.gonabad.taskmanagment.databinding.FragmentSettingAdminBindingimport ir.gonabad.taskmanagment.utils.BaseFragment@AndroidEntryPointclass SettingAdminFragment : BaseFragment(){    lateinit var binding : FragmentSettingAdminBinding    val viewModel : SettingAdminViewModel by viewModels()    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        binding = FragmentSettingAdminBinding.inflate(inflater , container , false)        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        initView()    }    private fun initView() {        binding.tbSettingAdmin.setNavigationOnClickListener {            findNavController().popBackStack()        }        binding.cvSettingAdminAddUser.setOnClickListener {            findNavController().navigate(R.id.action_settingAdminFragment_to_addUserFragment)        }        binding.cvSettingAdminUserList.setOnClickListener {            findNavController().navigate(R.id.action_settingAdminFragment_to_userListFragment)        }    }}