package ir.gonabad.taskmanagment.presentation.userListimport android.view.LayoutInflaterimport android.view.MenuItemimport android.view.ViewGroupimport androidx.appcompat.widget.PopupMenuimport androidx.recyclerview.widget.RecyclerViewimport ir.gonabad.taskmanagment.Rimport ir.gonabad.taskmanagment.databinding.ItemUserBindingclass UserAdapter : RecyclerView.Adapter<UserAdapter.UserAdapterViewHolder>() {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): UserAdapterViewHolder {        return UserAdapterViewHolder(ItemUserBinding.inflate(LayoutInflater.from(parent.context) , parent , false))    }    override fun getItemCount(): Int {        return 15    }    override fun onBindViewHolder(holder: UserAdapterViewHolder, position: Int) {        holder.onBind()    }    inner class UserAdapterViewHolder(private val itemUserBinding: ItemUserBinding) : RecyclerView.ViewHolder(itemUserBinding.root){        fun onBind() {            itemUserBinding.ivItemUserMenu.setOnClickListener {                val popup = PopupMenu(itemUserBinding.root.context , itemUserBinding.ivItemUserMenu)                popup.menuInflater.inflate(R.menu.user_menu , popup.menu)                popup.menu.getItem(1).isVisible = false                popup.setOnMenuItemClickListener {                    return@setOnMenuItemClickListener false                }                popup.show()            }        }    }}