package ir.gonabad.taskmanagment.presentation.userListimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.fragment.app.viewModelsimport androidx.navigation.fragment.findNavControllerimport androidx.recyclerview.widget.LinearLayoutManagerimport dagger.hilt.android.AndroidEntryPointimport ir.gonabad.taskmanagment.databinding.FragmentUserListBindingimport ir.gonabad.taskmanagment.utils.BaseFragment@AndroidEntryPointclass UserListFragment : BaseFragment() {    lateinit var binding : FragmentUserListBinding    val viewModel : UserListViewModel by viewModels()    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        binding = FragmentUserListBinding.inflate(inflater , container , false)        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        initView()    }    private fun initView() {        binding.tbUserList.setNavigationOnClickListener {            findNavController().popBackStack()        }        binding.rvUserList.layoutManager = LinearLayoutManager(requireContext() , LinearLayoutManager.VERTICAL , false)        binding.rvUserList.adapter = UserAdapter()    }}