package ir.gonabad.taskmanagment.utilsimport android.content.Contextimport android.net.ConnectivityManagerimport android.net.NetworkCapabilitiesimport android.os.Buildimport javax.inject.Injectclass IsOnline @Inject constructor(private val context: Context) {    fun isOnline() : Boolean{        val cm = context.getSystemService(Context.CONNECTIVITY_SERVICE) as ConnectivityManager        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.M) {            val n = cm.activeNetwork            if (n != null) {                val nc = cm.getNetworkCapabilities(n)                nc?.let {                    return nc.hasTransport(NetworkCapabilities.TRANSPORT_CELLULAR) || nc.hasTransport(                        NetworkCapabilities.TRANSPORT_WIFI                    )                }            }            return false        } else {            val netInfo = cm.activeNetworkInfo            return netInfo != null && netInfo.isConnectedOrConnecting        }    }}