package ir.gonabad.taskmanagment.utilsobject UserInfoContainer {    var token: String? = null        private set    var typeAccount : String ?= null        private set    var username : String ?= null        private set    fun update(token: String?) {        UserInfoContainer.token = token    }    fun setType( type : String) {        this.typeAccount = type    }    fun setUsername(username : String) {        this.username = username    }}