package ir.gonabad.taskmanagment.utilsimport android.content.Contextimport android.util.TypedValueimport androidx.annotation.AttrResimport androidx.annotation.ColorIntimport dagger.hilt.android.qualifiers.ApplicationContext@ColorIntfun getValueFromTheme(@ApplicationContext context: Context, @AttrRes resId: Int): Int {    val typedValue = TypedValue()    context.theme.resolveAttribute(resId, typedValue, true)    return typedValue.data}